#!/bin/sh

ROOT=`pwd`;
TMP=${ROOT}/tmp;
UPSTREAM_PROJECT=${ROOT}/upstream_project

echo '-----------------------------------env---------------------------------------'
env
echo '-----------------------------------pwd---------------------------------------'
pwd
echo '-----------------------------------home---------------------------------------'
echo $HOME
echo '-----------------------------------ls home/runner---------------------------------------'
ls -la /home/runner
